import { View } from 'react-native';
import { useLinkBuilder } from '@react-navigation/native';
import { Text, PlatformPressable } from '@react-navigation/elements';
import {
  createBottomTabNavigator,
  BottomTabBarProps,
} from '@react-navigation/bottom-tabs';
import Home from '../screens/Home';
import GroupsStack from './GroupsStack';
import AddExpense from '../screens/AddExpense';
import Messages from '../screens/Messages';
import Profile from '../screens/Profile';
import { lightTheme } from '../utility/themeColors';
import { useMemo } from 'react';
import { useAppSelector } from '../store/hooks';
import Icon from '../components/Icon';

type RootTabParamList = {
  Home: undefined;
  Groups: undefined;
  Add: undefined;
  Messages: undefined;
  Profile: undefined;
};

function CustomTabBar(props: any) {
  return <MyTabBar {...props} />;
}

const Tab = createBottomTabNavigator<RootTabParamList>();

function MyTabBar({ state, descriptors, navigation }: BottomTabBarProps) {
  const theme = lightTheme;
  const { buildHref } = useLinkBuilder();

  return (
    <View
      style={{
        flexDirection: 'row',
        backgroundColor: theme.background,
        borderTopWidth: 1,
        borderTopColor: theme.border || '#e0e0e0',
        paddingBottom: 5,
      }}
    >
      {state.routes.map((route, index) => {
        const { options } = descriptors[route.key];
        const label =
          options.tabBarLabel ?? undefined
            ? options.tabBarLabel
            : options.title ?? undefined
            ? options.title
            : route.name;

        const isFocused = state.index === index;

        const onPress = () => {
          const event = navigation.emit({
            type: 'tabPress',
            target: route.key,
            canPreventDefault: true,
          });

          if (!isFocused && !event.defaultPrevented) {
            navigation.navigate(route.name, route.params);
          }
        };

        const onLongPress = () => {
          navigation.emit({
            type: 'tabLongPress',
            target: route.key,
          });
        };

        return (
          <PlatformPressable
            key={route.key}
            href={buildHref(route.name, route.params)}
            accessibilityState={isFocused ? { selected: true } : {}}
            accessibilityLabel={options.tabBarAccessibilityLabel}
            testID={options.tabBarButtonTestID}
            onPress={onPress}
            onLongPress={onLongPress}
            style={{
              flex: 1,
              alignItems: 'center',
              justifyContent: 'center',
              paddingVertical: 10,
            }}
          >
            {options.tabBarIcon?.({
              focused: isFocused,
              color: isFocused ? theme.primary : theme.text,
              size: 24,
            })}

            <Text
              style={{
                color: isFocused ? theme.primary : theme.text,
                fontSize: 11,
                fontFamily: 'GoogleSans-Regular',
              }}
            >
              {typeof label === 'string' ? label : route.name}
            </Text>
          </PlatformPressable>
        );
      })}
    </View>
  );
}

export default function BottomTabs() {
  const theme = lightTheme;
  const token = useAppSelector(state => state.auth.token);

  const screenOptions = useMemo(
    () => ({
      Home: {
        headerTitle: 'SPLITWISE +',
        headerTitleStyle: {
          fontFamily: 'GoogleSans-BoldItalic',
          color: '#104d98',
          fontSize: 18,
          letterSpacing: 1,
        },
        headerStyle: {
          backgroundColor: '#f8fafc',
        },
        headerShadowVisible: false,
        tabBarIcon: ({
          color,
          focused,
        }: {
          color: string;
          focused: boolean;
        }) => (
          <View
            key={`home-${focused}`} // Add key based on focused state
            style={{
              paddingHorizontal: 15,
              paddingVertical: 5,
              borderRadius: 100,
              backgroundColor: focused ? theme.primary + '20' : 'transparent',
            }}
          >
            <Icon name="Home" width={22} height={22} fill={color} />
          </View>
        ),
      },
      Groups: {
        tabBarIcon: ({
          color,
          focused,
        }: {
          color: string;
          focused: boolean;
        }) => (
          <View
            key={`groups-${focused}`}
            style={{
              paddingHorizontal: 15,
              paddingVertical: 5,
              borderRadius: 100,
              backgroundColor: focused ? theme.primary + '20' : 'transparent',
            }}
          >
            <Icon name="Groups" width={22} height={22} fill={color} />
          </View>
        ),
      },
      AddExpense: {
        tabBarIcon: ({
          color,
          focused,
        }: {
          color: string;
          focused: boolean;
        }) => (
          <View
            key={`add-${focused}`}
            style={{
              paddingHorizontal: 15,
              paddingVertical: 5,
              borderRadius: 100,
              backgroundColor: focused ? theme.primary + '20' : 'transparent',
            }}
          >
            <Icon name="Add" width={22} height={22} fill={color} />
          </View>
        ),
      },
      Messages: {
        tabBarIcon: ({
          color,
          focused,
        }: {
          color: string;
          focused: boolean;
        }) => (
          <View
            key={`message-${focused}`}
            style={{
              paddingHorizontal: 15,
              paddingVertical: 5,
              borderRadius: 100,
              backgroundColor: focused ? theme.primary + '20' : 'transparent',
            }}
          >
            <Icon name="Message" width={22} height={22} fill={color} />
          </View>
        ),
      },
      Profile: {
        tabBarIcon: ({
          color,
          focused,
        }: {
          color: string;
          focused: boolean;
        }) => (
          <View
            key={`profile-${focused}`}
            style={{
              paddingHorizontal: 15,
              paddingVertical: 5,
              borderRadius: 100,
              backgroundColor: focused ? theme.primary + '20' : 'transparent',
            }}
          >
            <Icon name="Profile" width={22} height={22} fill={color} />
          </View>
        ),
      },
    }),
    [theme],
  );

  // AUTH GUARD
  if (!token) {
    return null;
  }

  return (
    <Tab.Navigator tabBar={CustomTabBar}>
      <Tab.Screen name="Home" component={Home} options={screenOptions.Home} />
      <Tab.Screen
        name="Groups"
        component={GroupsStack}
        options={screenOptions.Groups}
      />
      <Tab.Screen
        name="Add"
        component={AddExpense}
        options={screenOptions.AddExpense}
      />
      <Tab.Screen
        name="Messages"
        component={Messages}
        options={screenOptions.Messages}
      />
      <Tab.Screen
        name="Profile"
        component={Profile}
        options={screenOptions.Profile}
      />
    </Tab.Navigator>
  );
}

import React, { useState, useCallback } from 'react';
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
  FlatList,
  ActivityIndicator,
  Alert,
  ScrollView,
  KeyboardAvoidingView,
  Platform,
} from 'react-native';
import { useMutation, useLazyQuery } from '@apollo/client/react';
import { CREATE_GROUP, SEARCH_USERS } from '../graphql';
import type { NativeStackScreenProps } from '@react-navigation/native-stack';
import type { GroupsStackParamList } from '../navigations/GroupsStack';

type Props = NativeStackScreenProps<GroupsStackParamList, 'CreateGroup'>;

type UserResult = {
  id: string;
  name: string;
  email: string;
  imageUrl?: string;
};

const CreateGroup: React.FC<Props> = ({ navigation }) => {
  const [groupName, setGroupName] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedMembers, setSelectedMembers] = useState<UserResult[]>([]);

  const [searchUsers, { data: searchData, loading: searching }] =
    useLazyQuery<any>(SEARCH_USERS);

  const [createGroup, { loading: creating }] = useMutation<any>(CREATE_GROUP, {
    onCompleted: () => {
      Alert.alert('Success', 'Group created successfully!');
      navigation.goBack();
    },
    onError: (err: any) => {
      Alert.alert('Error', err.message);
    },
  });

  const handleSearch = useCallback(
    (text: string) => {
      setSearchQuery(text);
      if (text.trim().length >= 2) {
        searchUsers({ variables: { query: text.trim() } });
      }
    },
    [searchUsers],
  );

  const addMember = (user: UserResult) => {
    if (selectedMembers.find(m => m.id === user.id)) return;
    setSelectedMembers(prev => [...prev, user]);
    setSearchQuery('');
  };

  const removeMember = (userId: string) => {
    setSelectedMembers(prev => prev.filter(m => m.id !== userId));
  };

  const handleCreate = () => {
    if (!groupName.trim()) {
      Alert.alert('Validation Error', 'Group name cannot be empty');
      return;
    }
    const emails = selectedMembers.map(m => m.email);
    createGroup({
      variables: { name: groupName.trim(), memberEmails: emails },
    });
  };

  const searchResults = (searchData?.searchUsers || []).filter(
    (u: UserResult) => !selectedMembers.find(m => m.id === u.id),
  );

  return (
    <KeyboardAvoidingView
      style={styles.container}
      behavior={Platform.OS === 'ios' ? 'padding' : undefined}
    >
      <ScrollView contentContainerStyle={styles.content}>
        <Text style={styles.label}>Group Name</Text>
        <TextInput
          style={styles.input}
          placeholder="e.g., Goa Trip 2026"
          value={groupName}
          onChangeText={setGroupName}
          placeholderTextColor="#94a3b8"
        />

        <Text style={styles.label}>Add Members</Text>
        <TextInput
          style={styles.input}
          placeholder="Search by email..."
          value={searchQuery}
          onChangeText={handleSearch}
          placeholderTextColor="#94a3b8"
          keyboardType="email-address"
          autoCapitalize="none"
        />

        {searching && (
          <ActivityIndicator
            size="small"
            color="#667eea"
            style={styles.searchingIndicator}
          />
        )}

        {searchQuery.length >= 2 && searchResults.length > 0 && (
          <View style={styles.searchResults}>
            {searchResults.map((user: UserResult) => (
              <TouchableOpacity
                key={user.id}
                style={styles.searchResultItem}
                onPress={() => addMember(user)}
              >
                <View style={styles.resultAvatar}>
                  <Text style={styles.resultAvatarText}>
                    {user.name.charAt(0).toUpperCase()}
                  </Text>
                </View>
                <View style={styles.resultInfo}>
                  <Text style={styles.resultName}>{user.name}</Text>
                  <Text style={styles.resultEmail}>{user.email}</Text>
                </View>
                <Text style={styles.addIcon}>+</Text>
              </TouchableOpacity>
            ))}
          </View>
        )}

        {searchQuery.length >= 2 &&
          searchResults.length === 0 &&
          !searching && <Text style={styles.noResults}>No users found</Text>}

        {selectedMembers.length > 0 && (
          <View style={styles.chipsSection}>
            <Text style={styles.chipsLabel}>
              Selected ({selectedMembers.length})
            </Text>
            <View style={styles.chipsContainer}>
              {selectedMembers.map(member => (
                <View key={member.id} style={styles.chip}>
                  <Text style={styles.chipText}>{member.name}</Text>
                  <TouchableOpacity onPress={() => removeMember(member.id)}>
                    <Text style={styles.chipRemove}>Γ£ò</Text>
                  </TouchableOpacity>
                </View>
              ))}
            </View>
          </View>
        )}
      </ScrollView>

      <View style={styles.footer}>
        <TouchableOpacity
          style={[styles.createBtn, creating && styles.createBtnDisabled]}
          onPress={handleCreate}
          disabled={creating}
        >
          <Text style={styles.createBtnText}>
            {creating ? 'Creating...' : 'Create Group'}
          </Text>
        </TouchableOpacity>
      </View>
    </KeyboardAvoidingView>
  );
};

export default CreateGroup;

const styles = StyleSheet.create({
  container: { flex: 1, backgroundColor: '#f8fafc' },
  content: { padding: 20, paddingBottom: 100 },
  label: {
    fontSize: 14,
    fontWeight: '700',
    color: '#475569',
    marginBottom: 8,
    marginTop: 16,
  },
  input: {
    borderWidth: 1,
    borderColor: '#cbd5e1',
    borderRadius: 12,
    padding: 14,
    fontSize: 16,
    backgroundColor: '#fff',
    color: '#1e293b',
  },
  searchingIndicator: { marginTop: 12 },
  searchResults: {
    marginTop: 8,
    backgroundColor: '#fff',
    borderRadius: 12,
    borderWidth: 1,
    borderColor: '#e2e8f0',
    overflow: 'hidden',
  },
  searchResultItem: {
    flexDirection: 'row',
    alignItems: 'center',
    padding: 14,
    borderBottomWidth: 1,
    borderBottomColor: '#f1f5f9',
  },
  resultAvatar: {
    width: 40,
    height: 40,
    borderRadius: 20,
    backgroundColor: '#e0e7ff',
    justifyContent: 'center',
    alignItems: 'center',
    marginRight: 12,
  },
  resultAvatarText: { fontSize: 16, fontWeight: '700', color: '#667eea' },
  resultInfo: { flex: 1 },
  resultName: { fontSize: 15, fontWeight: '600', color: '#1e293b' },
  resultEmail: { fontSize: 13, color: '#64748b', marginTop: 2 },
  addIcon: { fontSize: 22, color: '#667eea', fontWeight: '700' },
  noResults: {
    marginTop: 12,
    color: '#94a3b8',
    fontSize: 14,
    textAlign: 'center',
    fontStyle: 'italic',
  },
  chipsSection: { marginTop: 24 },
  chipsLabel: {
    fontSize: 14,
    fontWeight: '700',
    color: '#475569',
    marginBottom: 10,
  },
  chipsContainer: { flexDirection: 'row', flexWrap: 'wrap', gap: 8 },
  chip: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#e0e7ff',
    paddingHorizontal: 14,
    paddingVertical: 8,
    borderRadius: 20,
    gap: 6,
  },
  chipText: { fontSize: 14, color: '#3730a3', fontWeight: '600' },
  chipRemove: { fontSize: 14, color: '#6366f1', fontWeight: '700' },
  footer: {
    position: 'absolute',
    bottom: 0,
    left: 0,
    right: 0,
    padding: 20,
    backgroundColor: '#f8fafc',
    borderTopWidth: 1,
    borderTopColor: '#e2e8f0',
  },
  createBtn: {
    backgroundColor: '#667eea',
    padding: 16,
    borderRadius: 12,
    alignItems: 'center',
  },
  createBtnDisabled: { opacity: 0.6 },
  createBtnText: { color: '#fff', fontSize: 16, fontWeight: '700' },
});

import React, { useState } from 'react';
import {
  StyleSheet,
  Text,
  View,
  FlatList,
  TouchableOpacity,
  ActivityIndicator,
  Alert,
  Modal,
  TextInput,
} from 'react-native';
import { useQuery, useMutation } from '@apollo/client/react';
import {
  GET_GROUP_DETAILS,
  GET_GROUP_EXPENSES,
  INVITE_TO_GROUP,
} from '../graphql';
import type { NativeStackScreenProps } from '@react-navigation/native-stack';
import type { GroupsStackParamList } from '../navigations/GroupsStack';

type Props = NativeStackScreenProps<GroupsStackParamList, 'GroupDetail'>;

const GroupDetail: React.FC<Props> = ({ route }) => {
  const { groupId } = route.params;

  const {
    data: groupData,
    loading: loadingGroup,
    refetch: refetchGroup,
  } = useQuery<any>(GET_GROUP_DETAILS, { variables: { id: groupId } });

  const { data: expensesData, loading: loadingExpenses } = useQuery<any>(
    GET_GROUP_EXPENSES,
    { variables: { groupId } },
  );

  const [inviteModalVisible, setInviteModalVisible] = useState(false);
  const [inviteEmail, setInviteEmail] = useState('');

  const [inviteToGroup, { loading: inviting }] = useMutation<any>(
    INVITE_TO_GROUP,
    {
      onCompleted: () => {
        setInviteModalVisible(false);
        setInviteEmail('');
        Alert.alert('Success', 'Invitation sent!');
      },
      onError: (err: any) => {
        Alert.alert('Error', err.message);
      },
    },
  );

  const handleInvite = () => {
    if (!inviteEmail.trim()) {
      Alert.alert('Error', 'Email cannot be empty');
      return;
    }
    inviteToGroup({ variables: { groupId, email: inviteEmail.trim() } });
  };

  if (loadingGroup && !groupData) {
    return (
      <View style={styles.center}>
        <ActivityIndicator size="large" color="#667eea" />
      </View>
    );
  }

  const group = groupData?.getGroupDetails;
  const expenses = expensesData?.getGroupExpenses || [];

  if (!group) {
    return (
      <View style={styles.center}>
        <Text style={styles.errorText}>Group not found</Text>
      </View>
    );
  }

  const renderMember = ({ item }: any) => (
    <View style={styles.memberItem}>
      <View style={styles.memberAvatar}>
        <Text style={styles.memberAvatarText}>
          {item.user.name.charAt(0).toUpperCase()}
        </Text>
      </View>
      <View style={styles.memberInfo}>
        <Text style={styles.memberName}>{item.user.name}</Text>
        <Text style={styles.memberEmail}>{item.user.email}</Text>
      </View>
      <Text style={styles.memberRole}>{item.role}</Text>
    </View>
  );

  const renderExpense = ({ item }: any) => (
    <View style={styles.expenseCard}>
      <View style={styles.expenseInfo}>
        <Text style={styles.expenseDesc}>{item.note || 'Expense'}</Text>
        <Text style={styles.expenseBy}>
          by {item.createdBy?.name || 'Unknown'}
        </Text>
      </View>
      <Text style={styles.expenseAmount}>
        Γé╣{parseFloat(item.totalAmount).toFixed(2)}
      </Text>
    </View>
  );

  const ListHeader = () => (
    <View>
      {/* Group Info */}
      <View style={styles.groupHeader}>
        <View style={styles.groupIcon}>
          <Text style={styles.groupIconText}>
            {group.name?.charAt(0).toUpperCase()}
          </Text>
        </View>
        <Text style={styles.groupName}>{group.name}</Text>
        {group.description && (
          <Text style={styles.groupDescription}>{group.description}</Text>
        )}
      </View>

      {/* Members Section */}
      <View style={styles.sectionHeader}>
        <Text style={styles.sectionTitle}>
          Members ({group.members.length})
        </Text>
        <TouchableOpacity
          style={styles.inviteBtn}
          onPress={() => setInviteModalVisible(true)}
        >
          <Text style={styles.inviteBtnText}>+ Invite</Text>
        </TouchableOpacity>
      </View>

      <FlatList
        data={group.members}
        keyExtractor={(item: any) => item.id}
        renderItem={renderMember}
        scrollEnabled={false}
        style={styles.membersList}
      />

      {/* Expenses Section */}
      <View style={styles.sectionHeader}>
        <Text style={styles.sectionTitle}>Expenses ({expenses.length})</Text>
      </View>

      {loadingExpenses && (
        <ActivityIndicator
          size="small"
          color="#667eea"
          style={styles.expenseLoader}
        />
      )}

      {!loadingExpenses && expenses.length === 0 && (
        <View style={styles.emptyExpenses}>
          <Text style={styles.emptyText}>No expenses yet</Text>
        </View>
      )}
    </View>
  );

  return (
    <View style={styles.container}>
      <FlatList
        data={expenses}
        keyExtractor={(item: any) => item.id}
        renderItem={renderExpense}
        ListHeaderComponent={ListHeader}
        refreshing={loadingGroup}
        onRefresh={refetchGroup}
        contentContainerStyle={styles.listContent}
      />

      {/* Invite Modal */}
      <Modal visible={inviteModalVisible} animationType="fade" transparent>
        <View style={styles.modalOverlay}>
          <View style={styles.modalContent}>
            <Text style={styles.modalTitle}>Invite Member</Text>
            <TextInput
              style={styles.modalInput}
              placeholder="Enter email address"
              value={inviteEmail}
              onChangeText={setInviteEmail}
              keyboardType="email-address"
              autoCapitalize="none"
              placeholderTextColor="#94a3b8"
            />
            <View style={styles.modalActions}>
              <TouchableOpacity
                style={[styles.modalBtn, styles.modalBtnCancel]}
                onPress={() => setInviteModalVisible(false)}
                disabled={inviting}
              >
                <Text style={styles.modalBtnCancelText}>Cancel</Text>
              </TouchableOpacity>
              <TouchableOpacity
                style={[styles.modalBtn, styles.modalBtnSend]}
                onPress={handleInvite}
                disabled={inviting}
              >
                <Text style={styles.modalBtnSendText}>
                  {inviting ? 'Sending...' : 'Send Invite'}
                </Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>
      </Modal>
    </View>
  );
};

export default GroupDetail;

