import React, { useState } from 'react';
import {
  StyleSheet,
  Text,
  View,
  FlatList,
  TouchableOpacity,
  ActivityIndicator,
  Alert,
  Modal,
  TextInput,
  KeyboardAvoidingView,
  Platform,
} from 'react-native';
import { useQuery, useMutation } from '@apollo/client/react';
import {
  GET_GROUPS,
  GET_MY_INVITES,
  JOIN_GROUP,
  RESPOND_TO_INVITE,
} from '../graphql';
import { useNavigation } from '@react-navigation/native';
import type { NativeStackNavigationProp } from '@react-navigation/native-stack';
import type { GroupsStackParamList } from '../navigations/GroupsStack';

type NavProp = NativeStackNavigationProp<GroupsStackParamList, 'GroupsList'>;

const Groups = () => {
  const navigation = useNavigation<NavProp>();

  const { data, loading, refetch } = useQuery<any>(GET_GROUPS, {
    fetchPolicy: 'cache-and-network',
  });

  const {
    data: invitesData,
    loading: loadingInvites,
    refetch: refetchInvites,
  } = useQuery<any>(GET_MY_INVITES, {
    fetchPolicy: 'cache-and-network',
  });

  const [joinGroup, { loading: joining }] = useMutation<any>(JOIN_GROUP, {
    onCompleted: () => {
      setJoinModalVisible(false);
      setJoinToken('');
      refetch();
      Alert.alert('Success', 'Successfully joined the group!');
    },
    onError: (err: any) => {
      Alert.alert('Error', err.message);
    },
  });

  const [respondToInvite] = useMutation<any>(RESPOND_TO_INVITE, {
    onCompleted: () => {
      refetch();
      refetchInvites();
    },
    onError: (err: any) => {
      Alert.alert('Error', err.message);
    },
  });

  const [joinModalVisible, setJoinModalVisible] = useState(false);
  const [joinToken, setJoinToken] = useState('');

  const handleJoinGroup = () => {
    if (!joinToken.trim()) {
      Alert.alert('Validation Error', 'Invite token cannot be empty');
      return;
    }
    joinGroup({ variables: { token: joinToken.trim() } });
  };

  const handleAcceptInvite = (inviteId: string) => {
    respondToInvite({ variables: { inviteId, accept: true } });
  };

  const handleRejectInvite = (inviteId: string) => {
    Alert.alert(
      'Reject Invite',
      'Are you sure you want to reject this invitation?',
      [
        { text: 'Cancel', style: 'cancel' },
        {
          text: 'Reject',
          style: 'destructive',
          onPress: () =>
            respondToInvite({ variables: { inviteId, accept: false } }),
        },
      ],
    );
  };

  if (loading && !data) {
    return (
      <View style={styles.center}>
        <ActivityIndicator size="large" color="#667eea" />
      </View>
    );
  }

  const groups = data?.getGroups || [];
  const invites = invitesData?.getMyInvites || [];

  const ListHeader = () => (
    <View>
      {/* Pending Invitations */}
      {invites.length > 0 && (
        <View style={styles.invitesSection}>
          <Text style={styles.invitesSectionTitle}>
            Pending Invitations ({invites.length})
          </Text>
          {invites.map((invite: any) => (
            <View key={invite.id} style={styles.inviteCard}>
              <View style={styles.inviteInfo}>
                <Text style={styles.inviteGroupName}>
                  {invite.group?.name || 'Unknown Group'}
                </Text>
                <Text style={styles.inviteMembers}>
                  {invite.group?.members?.length || 0} members
                </Text>
              </View>
              <View style={styles.inviteActions}>
                <TouchableOpacity
                  style={styles.acceptBtn}
                  onPress={() => handleAcceptInvite(invite.id)}
                >
                  <Text style={styles.acceptBtnText}>Accept</Text>
                </TouchableOpacity>
                <TouchableOpacity
                  style={styles.rejectBtn}
                  onPress={() => handleRejectInvite(invite.id)}
                >
                  <Text style={styles.rejectBtnText}>Reject</Text>
                </TouchableOpacity>
              </View>
            </View>
          ))}
        </View>
      )}

      {groups.length === 0 && (
        <View style={styles.emptyContainer}>
          <Text style={styles.emptyText}>
            You are not part of any groups yet.
          </Text>
          <Text style={styles.emptySubText}>
            Create one to get started splitting expenses!
          </Text>
        </View>
      )}
    </View>
  );

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.title}>Your Groups</Text>
        <View style={styles.headerButtons}>
          <TouchableOpacity
            style={[styles.addButton, styles.joinButton]}
            onPress={() => setJoinModalVisible(true)}
          >
            <Text style={styles.addButtonText}>Join</Text>
          </TouchableOpacity>
          <TouchableOpacity
            style={styles.addButton}
            onPress={() => navigation.navigate('CreateGroup')}
          >
            <Text style={styles.addButtonText}>+ New</Text>
          </TouchableOpacity>
        </View>
      </View>

      <FlatList
        data={groups}
        keyExtractor={(item: any) => item.id}
        refreshing={loading || loadingInvites}
        onRefresh={() => {
          refetch();
          refetchInvites();
        }}
        ListHeaderComponent={ListHeader}
        contentContainerStyle={styles.listContainer}
        renderItem={({ item }) => (
          <TouchableOpacity
            style={styles.groupCard}
            onPress={() =>
              navigation.navigate('GroupDetail', { groupId: item.id })
            }
            activeOpacity={0.7}
          >
            <View style={styles.groupIconContainer}>
              <Text style={styles.groupIconText}>
                {item.name.charAt(0).toUpperCase()}
              </Text>
            </View>
            <View style={styles.groupInfoContainer}>
              <Text style={styles.groupName}>{item.name}</Text>
              <Text style={styles.groupInfo}>
                {item.members.length} members
              </Text>
            </View>
            <Text style={styles.chevron}>ΓÇ║</Text>
          </TouchableOpacity>
        )}
      />

      {/* Join Group Modal */}
      <Modal visible={joinModalVisible} animationType="fade" transparent>
        <KeyboardAvoidingView
          behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
          style={styles.modalOverlay}
        >
          <View style={styles.modalContent}>
            <Text style={styles.modalTitle}>Join Group</Text>

            <TextInput
              style={styles.input}
              placeholder="Invite Token"
              value={joinToken}
              onChangeText={setJoinToken}
              placeholderTextColor="#999"
              autoCapitalize="none"
            />

            <View style={styles.modalActions}>
              <TouchableOpacity
                style={[styles.btn, styles.btnCancel]}
                onPress={() => setJoinModalVisible(false)}
                disabled={joining}
              >
                <Text style={styles.btnTextCancel}>Cancel</Text>
              </TouchableOpacity>
              <TouchableOpacity
                style={[styles.btn, styles.btnCreate]}
                onPress={handleJoinGroup}
                disabled={joining}
              >
                <Text style={styles.btnTextCreate}>
                  {joining ? 'Joining...' : 'Join Group'}
                </Text>
              </TouchableOpacity>
            </View>
          </View>
        </KeyboardAvoidingView>
      </Modal>
    </View>
  );
};

