import { View } from 'react-native';
import { useLinkBuilder } from '@react-navigation/native';
import { Text, PlatformPressable } from '@react-navigation/elements';
import { createBottomTabNavigator, BottomTabBarProps } from '@react-navigation/bottom-tabs';
import Home from '../screens/Home';
import Groups from '../screens/Groups';
import AddExpense from '../screens/AddExpense';
import Messages from '../screens/Messages';
import Profile from '../screens/Profile';
import { lightTheme } from '../utility/themeColors';
import { useMemo } from 'react';
import { useAppSelector } from "../store/hooks";
import Icon from '../components/Icon';

type RootTabParamList = {
  Home: undefined;
  Groups: undefined;
  Add: undefined;
  Messages: undefined;
  Profile: undefined;
};

function CustomTabBar(props: any) {
    return <MyTabBar {...props} />;
}

const Tab = createBottomTabNavigator<RootTabParamList>();

function MyTabBar({ state, descriptors, navigation }: BottomTabBarProps) {
    const theme = lightTheme;
    const { buildHref } = useLinkBuilder();

    return (
        <View style={{ 
            flexDirection: 'row',
            backgroundColor: theme.background,
            borderTopWidth: 1,
            borderTopColor: theme.border || '#e0e0e0',
            paddingBottom: 5
        }}>
            {state.routes.map((route, index) => {
                const { options } = descriptors[route.key];
                const label =
                    options.tabBarLabel ?? undefined
                        ? options.tabBarLabel
                        : options.title ?? undefined
                            ? options.title
                            : route.name;

                const isFocused = state.index === index;

                const onPress = () => {
                    const event = navigation.emit({
                        type: 'tabPress',
                        target: route.key,
                        canPreventDefault: true,
                    });

                    if (!isFocused && !event.defaultPrevented) {
                        navigation.navigate(route.name, route.params);
                    }
                };

                const onLongPress = () => {
                    navigation.emit({
                        type: 'tabLongPress',
                        target: route.key,
                    });
                };

                return (
                    <PlatformPressable
                        key={route.key}
                        href={buildHref(route.name, route.params)}
                        accessibilityState={isFocused ? { selected: true } : {}}
                        accessibilityLabel={options.tabBarAccessibilityLabel}
                        testID={options.tabBarButtonTestID}
                        onPress={onPress}
                        onLongPress={onLongPress}
                        style={{ flex: 1, alignItems: 'center', justifyContent: 'center', paddingVertical: 10 }}
                    >
                        {options.tabBarIcon?.({
                            focused: isFocused,
                            color: isFocused ? theme.primary : theme.text,
                            size: 24,
                        })}

                        <Text style={{ color: isFocused ? theme.primary : theme.text, fontSize: 11, fontFamily: 'GoogleSans-Regular', }}>
                            {typeof label === 'string' ? label : route.name}
                        </Text>
                    </PlatformPressable>
                );
            })}
        </View>
    );
}

export default function BottomTabs() {
    const theme = lightTheme;
    const token = useAppSelector((state) => state.auth.token);
    
    const screenOptions = useMemo(() => ({
        Home: {
            headerTitle: 'SPLITWISE +',
            headerTitleStyle: {
                fontFamily: 'GoogleSans-BoldItalic',
                color: "#104d98",
                fontSize: 18,
                letterSpacing: 1,
            },
            headerStyle:{
                backgroundColor: "#f8fafc",
            },
            headerShadowVisible: false,
            tabBarIcon: ({ color, focused }: { color: string; focused: boolean }) => (
                <View 
                    key={`home-${focused}`} // Add key based on focused state
                    style={{ 
                        paddingHorizontal: 15, 
                        paddingVertical: 5,
                        borderRadius: 100, 
                        backgroundColor: focused ? theme.primary + '20' : 'transparent'
                    }}
                >
                    <Icon name="Home" width={22} height={22} fill={color} />
                </View>
            ),
        },
        Groups: {
            tabBarIcon: ({ color, focused }: { color: string; focused: boolean }) => (
                <View 
                    key={`groups-${focused}`}
                    style={{ 
                        paddingHorizontal: 15, 
                        paddingVertical: 5,
                        borderRadius: 100, 
                        backgroundColor: focused ? theme.primary + '20' : 'transparent'
                    }}
                >
                    <Icon name="Groups" width={22} height={22} fill={color} />
                </View>
            ),
        },
        AddExpense: {
            tabBarIcon: ({ color, focused }: { color: string; focused: boolean }) => (
                <View 
                    key={`add-${focused}`}
                    style={{ 
                        paddingHorizontal: 15, 
                        paddingVertical: 5,
                        borderRadius: 100, 
                        backgroundColor: focused ? theme.primary + '20' : 'transparent'
                    }}
                >
                    <Icon name="Add" width={22} height={22} fill={color} />
                </View>
            ),
        },
        Messages: {
            tabBarIcon: ({ color, focused }: { color: string; focused: boolean }) => (
                <View 
                    key={`message-${focused}`}
                    style={{ 
                        paddingHorizontal: 15, 
                        paddingVertical: 5,
                        borderRadius: 100, 
                        backgroundColor: focused ? theme.primary + '20' : 'transparent',
                    }}
                >
                    <Icon name="Message" width={22} height={22} fill={color} />
                </View>
            ),
        },
        Profile: {
            tabBarIcon: ({ color, focused }: { color: string; focused: boolean }) => (
                <View 
                    key={`profile-${focused}`}
                    style={{ 
                        paddingHorizontal: 15, 
                        paddingVertical: 5,
                        borderRadius: 100, 
                        backgroundColor: focused ? theme.primary + '20' : 'transparent'
                    }}
                >
                    <Icon name="Profile" width={22} height={22} fill={color} />
                </View>
            ),
        },
    }), [theme]);

    // AUTH GUARD
    if (!token) {
        return null;
    }

    return (
        <Tab.Navigator tabBar={CustomTabBar}>
            <Tab.Screen name="Home" component={Home} options={screenOptions.Home} />
            <Tab.Screen name="Groups" component={Groups} options={screenOptions.Groups} />
            <Tab.Screen name="Add" component={AddExpense} options={screenOptions.AddExpense} />
            <Tab.Screen name="Messages" component={Messages} options={screenOptions.Messages} />
            <Tab.Screen name="Profile" component={Profile} options={screenOptions.Profile} />
        </Tab.Navigator>
    );
}

import React, { useState, useEffect } from 'react';
import {
  View,
  TouchableOpacity,
  StyleSheet,
  Image,
  Alert,
  Linking,
  KeyboardAvoidingView,
  Platform,
  Keyboard,
} from 'react-native';
import { useDispatch } from 'react-redux';
import InAppBrowser from 'react-native-inappbrowser-reborn';
import { authService } from '../services/auth.service';
import { setAuth } from '../store/authSlice';
import AppText from '../components/AppText';
import AppTextInput from '../components/AppTextInput';
import { API_URL } from '../apollo/client';

const openOAuth = async (url: string) => {
  if (await InAppBrowser.isAvailable()) {
    await InAppBrowser.open(url, {
      dismissButtonStyle: 'close',
      preferredBarTintColor: '#fff',
      preferredControlTintColor: '#000',
      showTitle: true,
      enableDefaultShare: false,
      ephemeralWebSession: false,
    });
  } else {
    Linking.openURL(url);
  }
};

const handleGoogleLogin = () => {
  openOAuth(`${API_URL}/auth/google`);
};

const handleGithubLogin = () => {
  openOAuth(`${API_URL}/auth/github`);
};

const Auth = () => {
  const dispatch = useDispatch();

  const [mode, setMode] = useState<'login' | 'signup'>('login');
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [keyboardVisible, setKeyboardVisible] = useState(false);

  // Γ£à Keyboard visibility listener
  useEffect(() => {
    const showSub = Keyboard.addListener('keyboardDidShow', () =>
      setKeyboardVisible(true),
    );
    const hideSub = Keyboard.addListener('keyboardDidHide', () =>
      setKeyboardVisible(false),
    );

    return () => {
      showSub.remove();
      hideSub.remove();
    };
  }, []);

  const handleSubmit = async () => {
    if (!email || !password || (mode === 'signup' && !name)) {
      Alert.alert('Error', 'Please fill all required fields');
      return;
    }

    if (mode === 'signup') {
      const nameRegex = /^[a-zA-Z\\s'-]{2,50}$/;
      if (!nameRegex.test(name.trim())) {
        Alert.alert(
          'Invalid Name',
          'Only letters, spaces, hyphens, and apostrophes are allowed (2-50 chars).',
        );
        return;
      }
    }

    try {
      setLoading(true);

      const result =
        mode === 'login'
          ? await authService.login(email, password)
          : await authService.signup(name, email, password);

      dispatch(
        setAuth({
          token: result.token,
          user: result.user,
        }),
      );
    } catch (err: any) {
      Alert.alert(
        'Authentication Failed',
        err?.message || 'Something went wrong',
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <KeyboardAvoidingView
      style={{ flex: 1 }}
      behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
    >
      <View style={styles.container}>
        <View style={styles.formContainer}>
          {/* Title */}
          <View style={styles.title}>
            <AppText style={styles.titleText}>
              {mode === 'login' ? 'Welcome Back ≡ƒæï' : 'Create Account Γ£¿'}
            </AppText>
          </View>

          {/* OAuth */}
          <TouchableOpacity
            onPress={handleGoogleLogin}
            style={styles.googleBtn}
            disabled={loading}
          >
            <Image
              source={require('../../assets/images/google-logo.png')}
              style={styles.oauthIcon}
            />
            <AppText style={styles.oauthText}>Continue with Google</AppText>
          </TouchableOpacity>

          <TouchableOpacity
            onPress={handleGithubLogin}
            style={styles.githubBtn}
            disabled={loading}
          >
            <Image
              source={require('../../assets/images/github-logo.png')}
              style={styles.oauthIcon}
            />
            <AppText style={styles.oauthText}>Continue with GitHub</AppText>
          </TouchableOpacity>

          <AppText style={styles.orText}>OR</AppText>

          {mode === 'signup' && (
            <AppTextInput
              placeholder="Name"
              value={name}
              onChangeText={setName}
              style={styles.input}
              placeholderTextColor="#999"
            />
          )}

          <AppTextInput
            placeholder="Email"
            value={email}
            onChangeText={setEmail}
            style={styles.input}
            placeholderTextColor="#999"
            keyboardType="email-address"
            autoCapitalize="none"
          />

          <AppTextInput
            placeholder="Password"
            value={password}
            onChangeText={setPassword}
            style={styles.input}
            placeholderTextColor="#999"
            secureTextEntry
          />

          <TouchableOpacity
            style={[styles.submitBtn, loading && { opacity: 0.7 }]}
            onPress={handleSubmit}
            disabled={loading}
          >
            <AppText style={styles.submitText}>
              {loading
                ? 'Please wait...'
                : mode === 'login'
                ? 'Login'
                : 'Sign Up'}
            </AppText>
          </TouchableOpacity>

          <TouchableOpacity
            onPress={() => setMode(mode === 'login' ? 'signup' : 'login')}
            disabled={loading}
          >
            <AppText style={styles.toggleText}>
              {mode === 'login'
                ? 'DonΓÇÖt have an account? Sign up'
                : 'Already have an account? Login'}
            </AppText>
          </TouchableOpacity>
        </View>

        {/* Γ£à Bottom logo hidden when keyboard opens */}
        {!keyboardVisible && (
          <View style={styles.bottomLogoContainer}>
            <Image
              source={require('../../assets/images/splitwise-logo.png')}
              style={styles.bottomLogo}
              resizeMode="contain"
            />
            <Image
              source={require('../../assets/images/logo-text.png')}
              style={styles.bottomLogoText}
              resizeMode="contain"
            />
          </View>
        )}
      </View>
    </KeyboardAvoidingView>
  );
};

export default Auth;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 24,
    paddingBottom: 50,
    backgroundColor: '#fff',
  },
  formContainer: {
    flex: 1,
    justifyContent: 'center',
  },
  title: {
    alignItems: 'center',
    marginBottom: 20,
  },
  titleText: {
    fontSize: 20,
  },
  googleBtn: {
    backgroundColor: '#ebe5e5ff',
    padding: 14,
    borderRadius: 10,
    marginBottom: 12,
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    gap: 12,
  },
  githubBtn: {
    backgroundColor: '#ebe5e5ff',
    padding: 14,
    borderRadius: 10,
    marginBottom: 20,
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    gap: 12,
  },
  oauthIcon: {
    width: 25,
    height: 25,
  },
  oauthText: {
    color: '#000',
    fontSize: 13,
  },
  orText: {
    textAlign: 'center',
    color: '#999',
    marginBottom: 16,
  },
  input: {
    borderWidth: 1,
    borderColor: '#ddd',
    borderRadius: 10,
    padding: 14,
    fontSize: 13,
    marginBottom: 12,
    color: '#000',
  },
  submitBtn: {
    backgroundColor: '#4f46e5',
    padding: 16,
    borderRadius: 12,
    marginTop: 8,
  },
  submitText: {
    color: '#fff',
    textAlign: 'center',
    fontSize: 13,
  },
  toggleText: {
    marginTop: 16,
    textAlign: 'center',
    color: '#4f46e5',
  },
  bottomLogoContainer: {
    position: 'absolute',
    bottom: 24,
    width: '100%',
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    alignSelf: 'center',
    gap: 10,
  },
  bottomLogo: {
    width: 36,
    height: 36,
  },
  bottomLogoText: {
    width: 120,
    height: 36,
  },
});

